<style lang="scss" scoped>
.admin-home__content__news__menu {
    position: absolute;
    top: 50%;
    right: 50%;
    transform: translate(50%, -50%);
    display: flex;
    flex-direction: row;
    gap: 37px;

    &__wrapper {
        width: 212px;
        height: 201px;
        border: 1.5px solid var(--admin-blog-box-bg);
        border-radius: 6px;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        cursor: pointer;

        &:hover,
        &:focus {
            color: var(--sub-font-color);
            background-color: var(--hover-bg-color);

            :nth-child(1) {
                path {
                    fill: var(--sub-font-color);
                }
            }
        }

        &__icon {
            width: 83px;
            height: 52px;
        }

        &__title {
            font-weight: 600;
            font-size: 16px;
            line-height: 58px;
            letter-spacing: 0.15em;
        }

    }
}
</style>

